import Button from '../Button/Button';
import styles from './AddPostForm.module.scss';
import { useState } from 'react';

const AddPostForm = () => {

    const [title, setTitle] = useState('');


    return (
    <form className={styles.root}>
        <p>Title</p>
        <input value={title} onChange={e => setTitle(e.target.value)} type="title" className="title" placeholder=" Enter title" />
        <p>Author</p>
        <input type="author" className="author" placeholder=" Enter author" />
        <p>Published</p>
        <input type="date" className="date" />
        <p>Short description</p>
        <textarea id="exampleFormControlTextarea1"  className="description" placeholder=" Leave a short summery here"rows="1"></textarea>
        <p>Main contenet VISYA</p>
        <textarea className="content" placeholder=" Write your blog here" id="exampleFormControlTextarea1" rows="3"></textarea>
        <Button>Add Post</Button>
    </form>
    )
}

export default AddPostForm;
